using UnityEngine;using System.Collections;using System.Collections.Generic;public class config_monster_item : config_item_base{    public int hide_id;	public int monster_type;	public int size;    public int rotate;    public bool forever;    public string release;    override public void Clear()    {        base.Clear();		monster_type = 0;        forever = false;        size = 0;        rotate = 0;        release = "";    }    public List<TIVInfo> GetReleaseList()    {        List<TIVInfo> releases = new List<TIVInfo>();        string[] strs = release.Split(new char[] { ',' });        for (int i = 0; i < strs.Length; i++)        {            string str = strs[i];            string[] str2s = str.Split(new char[] { '|' });            TIVInfo tiv = new TIVInfo();            tiv.id = int.Parse(str2s[0]);			tiv.value = float.Parse(str2s[1]);            releases.Add(tiv);        }        return releases;    }}